sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var rightnow = new GlideDateTime();
	gs.addInfoMessage("Right Now is " + rightnow);
	var whenneeded =  new GlideDateTime(current.u_when_needed);
	gs.addInfoMessage("When Needed is "+whenneeded);

	var result_1 = whenneeded.before(rightnow);
	gs.addInfoMessage("Before api result is "+ result_1);	
	if(result_1)
		{
			gs.addErrorMessage("When needed date cannot be before current date!");
			current.setAbortAction(true);
		}


	var rightnow_date = rightnow.getDate();
	var whenneeded_date = whenneeded.getDate();

	var result = rightnow_date.compareTo(whenneeded_date);
	if(result==0)
		{
			gs.addErrorMessage("When needed date cannot be requested for today");
			current.setAbortAction(true);
		}
	gs.addInfoMessage("Compare to resuls is "+ result);

})(current, previous);
